const BlogCard=(props)=>{
    const {title, description, link}=props;

    return(
        <div className="blogcard">
            <h3>{title}</h3>
            <p>{description}</p>
             <img src={link} alt={title} style={{ width: '100%', height: 'auto' }} />
        </div>
    );
}

export default BlogCard;
